const transcript = $input.first().json.transcript;
const url = $input.first().json.url;

const prompt = `You are an expert content analyzer. Analyze this YouTube video transcript and provide:

1. ONE-SENTENCE SUMMARY: Distill the core message in one sentence
2. KEY POINTS: List 3-5 main takeaways as bullet points
3. ACTION ITEMS: What concrete actions or recommendations does this suggest?
4. TOPICS: List relevant topics/categories (comma-separated)

Transcript:
${transcript}

Provide your response in this exact format:

SUMMARY: [one sentence]

KEY POINTS:
- [point 1]
- [point 2]
- [point 3]

ACTION ITEMS:
- [action 1]
- [action 2]

TOPICS: [topic1, topic2, topic3]`;

return {
  json: {
    videoId: $input.first().json.videoId,
    url: url,
    transcript: transcript,
    wordCount: $input.first().json.wordCount,
    prompt: prompt
  }
};

const input = $input.first().json;

let llmResponse;
if (input.message && input.message.content) {
  llmResponse = input.message.content;
} else if (input.choices && input.choices[0]) {
  llmResponse = input.choices[0].message.content;
} else if (input.response) {
  llmResponse = input.response;
} else {
  llmResponse = JSON.stringify(input);
}

const videoData = $('Parse Transcript').first().json;

return {
  json: {
    success: true,
    videoId: videoData.videoId,
    url: videoData.url,
    summary: llmResponse,
    wordCount: videoData.wordCount,
    timestamp: new Date().toISOString()
  }
};
